mapper.update:
update address a, lessonbranch lb set a.deletekey=1, lb.deletekey=1
where a.qid=#{qid} and lb.branchid in
(select a_lb.branchid from 
	(select b.branchid from 
	 lessonbranch b left join address ad on b.branchid=ad.id 
	 where ad.qid=#{qid}
	) a_lb
)

update address set branch=#{branch}, address=#{address}, tel=#{tel},
				   longitude=#{longitude}, latitude=#{latitude}
where id=#{id}
