mapper.select:
select count(*) from coupon 
where qid=#{qid} and deletekey=0 

	and cid = #{cid}


	and category = #{category}


	and disabledkey = 0

select count(*) from ((select u.uid from user u left join sorder s on s.uid=u.uid left join lesson l on s.lid=l.lid left join freelistenbook fb on fb.uid=u.uid left join freelisten f on fb.fid=f.id where u.qid=1 and u.deletekey=0 and u.totalpoint>=#{su_points}
and (l.category=#{su_category} or f.category=#{su_category})
group by u.uid) m)

select u.uid from user u left join sorder s on s.uid=u.uid left join lesson l on s.lid=l.lid left join freelistenbook fb on fb.uid=u.uid left join freelisten f on fb.fid=f.id where u.qid=1 and u.deletekey=0 and u.totalpoint>=#{su_points}
and (l.category=#{su_category} or f.category=#{su_category})
group by u.uid
