mapper.update:
update freelisten fl, freelistenbook flb set fl.deletekey=1, flb.deletekey=1
where fl.branchid=#{branchid} and flb.fid in
(select f_fb.fid from 
	(select fid from 
	 freelistenbook fb left join freelisten f on fb.fid=f.id 
	 where f.branchid=#{branchid}
	) f_fb
)

update freelisten fl, freelistenbook flb set fl.deletekey=1, flb.deletekey=1
where fl.qid=#{qid} and flb.fid in
(select f_fb.fid from 
	(select fid from 
	 freelistenbook fb left join freelisten f on fb.fid=f.id 
	 where f.qid=#{qid}
	) f_fb
)

update freelisten
title = #{title}, fdesc = #{fdesc}, branchid = #{branchid}, status = #{status}, category = #{category},
imgurl = #{imgurl}
where id = #{id}

mapper.select:
select * from freelisten where qid=#{qid} and deletekey=0

	and branchid = #{branchid}

limit #{pageTool.startrow}, #{pageTool.limit}
