mapper.select:
select *
from lesson 
where qid=#{qid} 
 
		and #{page.startRowId}>lid

order by lid desc
limit 0,#{page.pageSize}

		select * from lesson join lessonbranch on lesson.lid = lessonbranch.lid 
		where deletekey = 0 
		 
				and #{page.startRowId}>lesson.lid
		
		
			and lessonbranch.bid = #{bid}
		
		
			and lesson.category = #{category}
		
		 order by lid desc
         limit 0,#{page.pageSize}

		SELECT lesson.lid,imgurl from lesson,lessonbranch,address 
		where lesson.deletekey = 0 
			  and lesson.lid = lessonbranch.lid 
			  and lessonbranch.branchid = address.id
			  and lesson.qid=#{qid}
			  
				and #{page.startRowId}>lesson.lid
			
			 
				and address.branch = #{branchName}
			
			
				and category = #{category}
			
		order by lid desc
         limit 0,#{page.pageSize}
